;; **
;;; `loom-view` calls out to GraphViz to draw the graph. It takes the same options as loom's `view` function.
;; **

;; @@
(loom-view (r-wdg) :alg :neato)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/PNG;base64,\"/>","value":"#loom_gorilla.render.LoomView{:content #loom.graph.BasicEditableWeightedDigraph{:nodeset #{:0 :1 :2 :3}, :adj {:3 {:1 1}, :2 {:3 2}, :1 {:1 1, :0 1, :3 1}}, :in {:1 #{:1 :3}, :3 #{:1 :2}, :0 #{:1}}}, :opts (:alg :neato)}"}
