(->> words
     (mapcat w2cf)
     frequencies
     sort
     (map (juxt (comp first first)
                (comp second first)
                second))
     (map (juxt first
                second
                last
                #(* (second %) (last %))))
     (group-by first)
     (map (juxt key
                (comp #(reduce + %) #(map last %) val)))
    (sort-by second)
     (apply map list)
     (apply bar-chart)
     )
